document.addEventListener('DOMContentLoaded',function(){const shopLink=document.getElementById('shopLink');const dropdownMenu=document.getElementById('dropdownMenu');dropdownMenu.style.display='none';shopLink.addEventListener('click',function(event){event.preventDefault();dropdownMenu.style.display=dropdownMenu.style.display==='flex'?'none':'flex'});document.addEventListener('click',function(event){if(!shopLink.contains(event.target)&&!dropdownMenu.contains(event.target)){dropdownMenu.style.display='none'}})});document.addEventListener('DOMContentLoaded',()=>{const cart=JSON.parse(localStorage.getItem('cart'))||[];const cartModal=document.getElementById('cartModal');const cartItemsContainer=document.getElementById('cartItems');const totalPriceElement=document.getElementById('totalPrice');function updateCart(){cartItemsContainer.innerHTML='';let total=0;cart.forEach((item,index)=>{total+=item.price;const itemElement=document.createElement('div');itemElement.className='cart-item';itemElement.innerHTML=`
    <span>${item.name} - $${item.price}</span>
    <span class="remove-item" onclick="removeFromCart(${index})">Eliminar</span>
`;cartItemsContainer.appendChild(itemElement)});totalPriceElement.innerText=`Total: $${total}`;localStorage.setItem('cart',JSON.stringify(cart))}
window.addToCart=function(product){cart.push(product);updateCart()};window.removeFromCart=function(index){cart.splice(index,1);updateCart()};document.querySelector('.icon-cart').addEventListener('click',()=>{cartModal.classList.toggle('active')});window.addEventListener('click',(event)=>{const isClickOnCartIcon=document.querySelector('.icon-cart').contains(event.target);const isClickInsideCart=cartModal.contains(event.target);const isClickOnDeleteButton=event.target.classList.contains('remove-item');if(!isClickOnCartIcon&&!isClickInsideCart&&!isClickOnDeleteButton){cartModal.classList.remove('active')}});document.querySelectorAll('.shop-btn').forEach((button)=>{button.addEventListener('click',()=>{const productCard=button.parentElement;const productName=productCard.querySelector('h3').innerText;const productPrice=parseFloat(productCard.querySelector('p').innerText.replace('$','').replace('.',''))||0;addToCart({name:productName,price:productPrice})})});document.getElementById('checkoutBtn').addEventListener('click',()=>{alert("Oops, funcionalidad no disponible.")});updateCart()});document.addEventListener('DOMContentLoaded',function(){const filterButtons=document.querySelectorAll('.filter-btn');const products=document.querySelectorAll('.product-card');filterButtons.forEach(button=>{button.addEventListener('click',function(){filterButtons.forEach(btn=>btn.classList.remove('active'));this.classList.add('active');const category=this.getAttribute('data-category');products.forEach(product=>{if(category==='all'||product.getAttribute('data-category')===category){product.style.display='block'}else{product.style.display='none'}})})})})